<?phpfunction updateBill($entity){	require_once ("modules/Billing/Billing.php");	require_once ("modules/Payments/Payments.php");	$payment_arr = explode('x',$entity->id);	$payment_id = $payment_arr[1];		$payment_obj = new Payments();	$payment_obj->setColumns('Payments');	$payment_obj->id = $payment_id;	$payment_obj->retrieve_entity_info($payment_id,'Payments');		$bill_id = $payment_obj->column_fields['bill_no'];		$related_module = $_REQUEST['bill_no_type'];	if($related_module == 'MemoAdvice')		$status_field = 'ma_status';	else if($related_module == 'Billing')		$status_field = 'b_status';			$related_obj = new $related_module();	$related_obj->setColumns($related_module);	$related_obj->id = $bill_id;	$related_obj->retrieve_entity_info($bill_id,$related_module);		$payment = 	$related_obj->getAmountPayment($related_obj);	$related_obj->column_fields['amount_paid'] = $payment;		if($related_obj->column_fields['amount_paid'] >= $related_obj->column_fields['amount'])		$related_obj->column_fields[$status_field] = "Paid";	else		$related_obj->column_fields[$status_field] = "Pending";		$related_obj->mode = "edit";		$related_obj->save($related_module);}?>