<?phpfunction createClass($entity){	require_once('modules/ReportsKHA/ReportsHandler.php');	require_once('modules/ReportsKHA/ReportsKHA.php');	$report_arr = explode('x',$entity->id);	$report_id = $report_arr[1];		$report_obj = new ReportsKHA();	$report_obj->setColumns('ReportsKHA');	$report_obj->id = $report_id;	$report_obj->retrieve_entity_info($report_id,'ReportsKHA');		$rh = new ReportsHandler($report_obj);	$rh->initializeWriter();	$rh->createClass();	$rh->createTemplate();}?>