<?phpclass Detail extends ControllerCore{    protected $tableobj;        public function __construct(){    }        public function __initialize(){        $this->style('listview');        $this->setModel('Detail');        $this->setUtility('utilities');        $this->addLibrary('tables/TableTemplating');        $this->tableobj = new TableTemplating($this->dirPath);    }        public function index(){            $data = array('hello','bye','test');                $this->template('Common/Header',$data);        $this->template('Detail/Container');        $this->template('Common/Footer',$data);            }        public function view(){            if(isset($_REQUEST['record']))            $this->id = $_REQUEST['record'];                    $data['id'] = $this->id;        $data['data'] = $this->detail->getDetailTable($this->id);        $this->tableobj->setDisplay($data['data'][0]['template']);        $data['fields'] = addSeparator($this->detail->$data['data'][0]['template']($this->id));                        // $this->tableobj->getDisplay()->generateDisplay($data,'detail');                $data['table'] = $this->tableobj->getDisplay()->generateDisplay($data,'detail');		        //echo $data['data'][0]['cornerlabel'];                $this->template('Common/Header');        $this->template('Detail/Container',$data);        //$this->template('Common/Footer');            }    }?>