<?phpClass SingleXDoubleY{    public function __construct(){        }        public function generateDisplay($data2,$mode){        $header = parseHeader($data2['data'][0]['cornerlabel']);        $data = $data2['fields'];        $str = "<div>";        $str .="<table id='detailview' class='small' border=0 cellpadding=5 cellspacing=0 width='100%' align='center'>";        $str .="<tbody>";        $str .="<tr>                <th class='dvtCellLabel' rowspan=2>{$header->h1}</th>";            //column 0 row 0        foreach($data as $key=>$value)        {               $singleMonth = count($data[$key]);                        if(isset($data[$key]["SUMMARY"]) && $singleMonth == 2)                    $singleMonth1 = true;            else                $singleMonth1 = false;            foreach($value as $key2=>$value2)            {                $str .="<th class='dvtCellLabel' colspan=2>$key2</th>"; //Months row                if($singleMonth1 == true)                    break;              }            break;        }                $str .="</tr><tr>";              foreach($data as $key=>$value)        {            foreach($value as $key2=>$value2)            {                foreach($value2 as $key3=>$value3)                {                        $str .="<th class='dvtCellLabel'>$key3</th>";           //DoubleY row                }                if($singleMonth1 == true)                    break;            }            break;        }                $str .="</tr>";                foreach ($data as $key=>$value)        {            $str .="<tr><td class='dvtCellInfo'>$key</td>";             //Data Label (column0)                foreach($value as $key2=>$value2)                {                     foreach($value2 as $key3=>$value3)                    {                        $str .="<td class='dvtCellInfo'>$value3</td>";      // Data                    }                    if($singleMonth1 == true)                        break;                     }               $str .="</tr>";        }        $str .= "</tbody>";        $str .=" </table>";        $str .= "</div>";                return $str;    }}?>