<?phpClass SingleXSingleY{    public function __construct(){        }        public function generateDisplay($data2,$mode){                $header = parseHeader($data2['data'][0]['cornerlabel']);    $data = $data2['fields'];        $str = "<div style='overflow:auto;width:80%;'>";        $str .="<table class='small' border=0 cellpadding=5 cellspacing=0 width='100%' align='center'>";        $str .="<tbody>";        $str .= "<tr>";                    $str .= "<th class='dvtCellLabel' >{$header->h1}</th>";                foreach($data as $key=>$value)        {            foreach($value as $key2=>$value2)            {                $str.="<th class='dvtCellLabel' >$key2</th>";            }            break;        }        $str .= "</tr>";        foreach($data as $kdata => $vdata_arr)         {            $str .= "<tr>";            $str .= "<td class='dvtCellInfo'>".$kdata."</td>";            foreach($vdata_arr as $vdata)             {                $str .= "<td class='dvtCellInfo'>".$vdata."</td>";            }            $str .= "</tr>";        }        $str .= "</tbody>";        $str .= "</table>";        $str .="</div>";        return $str;    }}?>