<script>$(document).ready(function(){	});function goBack(){	window.history.back();}function save(){	var survey_name = jQuery('#survey_name').val();			 jQuery.ajax({                url:    "index.php?module=SurveyGen&action=index&controller=Create&controller_action=save&survey_name="+survey_name,                        type : 'post',                        data : {name:"John"},                beforeSend: function()                {                    //document.getElementById('chartContainer').innerHTML = '<center><img src="Image/9.gif" alt="Image/9.gif"/></center>';                 },                success: function(data)                 {					var myarr = data.split("{####???###}");					var id = myarr[1];										window.location = "index.php?module=SurveyGen&action=index&controller=Detail&controller_action=view&record="+jQuery.trim(id);                }        })	}</script><table border =1 id="container">	<tr>		<td>			Create Me		</td>	</tr>		<tr>		<td>			<table border =1 id ="FieldsContainer">				<tr>					<td>						<form>							<table border =1 id ="createFieldsContainer">								<tr>									<td>										Survey Name: 									</td>									<td>										<input type="text" id="survey_name" name="survey_name" />									</td>								</tr>								<tr>									<td>										<input type="button" value ="Save" onclick="save()"/>									</td>									<td>										<input type="button" value ="Cancel" onclick="goBack()"/>									</td>								</tr>							</table>						</form>					</td>				</tr>			</table>		</td>	</tr></table>